FB_DO_001(OUT	=>			DO_001,										(* couple the function block to the physical output *)
	PRIOHIGH				:=FALSE,  									(* brings the output high regardless of other input values *)
	PRIOLOW					:=FALSE, 									(* brings the output low regardless of other input values. NOTE: Priohigh overrules Priolow input *)
	TOGGLE					:=FB_DI_009.SINGLE 							(* for toggling the output *)	
);

FB_DO_002(OUT => DO_002		,PRIOHIGH:=FALSE			,PRIOLOW:=FALSE				,TOGGLE:=FB_DI_009.DOUBLE	);
FB_DO_003(OUT => DO_003		,PRIOHIGH:=FALSE			,PRIOLOW:=FALSE				,TOGGLE:=FB_DI_009.LONG		);
FB_DO_004(OUT => DO_004		,PRIOHIGH:=FALSE			,PRIOLOW:=FALSE				,TOGGLE:=FB_DI_009.LONG		);
