FB_DO_008(OUT => DO_008		,PRIOHIGH:=FALSE			,PRIOLOW:=FALSE				,TOGGLE:=FB_DI_009.LONG		);

FB_DI_001(DigitalInput:= DI_001); (* Provide the input FB the correct phyical input to validate each cyclus *)
