(* INIT MQTT STUFF *)
IF NOT MqttVariables.init THEN
	MqttVariables.init := TRUE;
	MqttVariables.MQTT_IN_OUT.ClientID := MqttVariables.clientID;
	FOR MqttVariables.icounter := 0 TO (MqttVariables.ipublishers-1) DO		
		MqttVariables.publishers[MqttVariables.icounter].Init(
			inputMQTT_IN_OUT:= ADR(MqttVariables.MQTT_IN_OUT),
			inputQoS:= SD_MQTT.QoS.ExactlyOnce, 
			inputRetainMqtt:= TRUE);
	END_FOR 
END_IF

(* INIT DIGITAL INPUT STUFF *)
FB_DI_001.InitMqtt(MQTTPublishPrefix:= ADR(MqttPushbuttonPrefix),	(* pointer to string prefix for the mqtt publish topic *)
