(* INIT DIGITAL OUTPUT STUFF *)
FB_DO_001.InitMqtt(MQTTPublishPrefix:= ADR(MqttSwitchPrefix),		(* pointer to string prefix for the mqtt publish topic *)
	MQTTPublishTopic := 'DO_001',									(* topic to publish to, get's prefixed with MQTTPublishPrefix *)
	pMqttPublishQueue := ADR(MqttVariables.fbMqttPublishQueue) 		(* pointer to MqttPublishQueue to send a new Mqtt event *)
);
