FB_DO_003.InitMqtt(MQTTPublishPrefix:= ADR(MqttSwitchPrefix),	MQTTPublishTopic := 'DO_003',	pMqttPublishQueue := ADR(MqttVariables.fbMqttPublishQueue));
FB_DO_004.InitMqtt(MQTTPublishPrefix:= ADR(MqttSwitchPrefix),	MQTTPublishTopic := 'DO_004',	pMqttPublishQueue := ADR(MqttVariables.fbMqttPublishQueue));
