FB_DO_008.InitMqtt(MQTTPublishPrefix:= ADR(MqttSwitchPrefix),	MQTTPublishTopic := 'DO_008',	pMqttPublishQueue := ADR(MqttVariables.fbMqttPublishQueue));

THIS^.MQTTPublishPrefix := MQTTPublishPrefix;
THIS^.MQTTPublishTopic := MQTTPublishTopic;
THIS^.pMqttPublishQueue := pMqttPublishQueue;

InitMqttDone := TRUE;

IF Startup THEN
	OUT := PreviousState;
END_IF

IF PRIOHIGH THEN
	OUT:=TRUE;
ELSIF(PRIOLOW AND NOT (PRIOHIGH)) THEN
	OUT:= FALSE;
ELSIF(TOGGLE) THEN
	OUT:=NOT(OUT);
END_IF;

IF Startup AND InitMqttDone THEN
	pMqttPublishQueue^.AddMessage(BOOL_TO_STRING(OUT), CONCAT(MQTTPublishPrefix^ ,MQTTPublishTopic), SD_MQTT.QoS.ExactlyOnce, TRUE);
	Startup := FALSE;
ELSIF(PreviousState <> OUT) AND InitMqttDone THEN
	pMqttPublishQueue^.AddMessage(BOOL_TO_STRING(OUT), CONCAT(MQTTPublishPrefix^ ,MQTTPublishTopic), SD_MQTT.QoS.ExactlyOnce, TRUE);
END_IF

PreviousState := OUT;

THIS^.MQTTPublishPrefix := MQTTPublishPrefix;
THIS^.MQTTPublishTopic := MQTTPublishTopic;
THIS^.pMqttPublishQueue := pMqttPublishQueue;

InitMqttDone := TRUE;

//need to call publish here because of FB nums
publish(
	Topic:= ADR(LocalTopic), 
	PayloadString:= ADR(LocalPayload), 
	PublishAsString:= TRUE, 
	QoSIn:= QoS, 
	MRetain:= RetainMqtt, 
	send:= Send);

IF NOT InitDone THEN RETURN; END_IF

IF RequestToSend THEN
	Send := TRUE;
	SendDone := FALSE;
END_IF

IF publish.done THEN
	 SendDone := TRUE;
	 RequestToSend:=FALSE;
	 Send:=FALSE;
END_IF

publish.SetMqttInOut(MQTT_IN_OUT := inputMQTT_IN_OUT);
THIS^.RetainMqtt := inputRetainMqtt;
THIS^.QoS := inputQoS;

InitDone := TRUE;
SendDone := TRUE;

IF MqttSendQueue.Peek() <> COL.COLLECTION_ERROR.NO_MORE_ELEMENTS THEN
	CheckifMessagesAvailable := TRUE;
ELSE
	CheckifMessagesAvailable := FALSE;
END_IF
